import React,{useState,useEffect} from 'react';
import "./style.css";
import Card from '../UI/Card';
import blogpostdata from '../../data/blog.json';
import Post from '../../containers/Post';

/**
* @author
* @function BlogPost

**/

const BlogPost= (props) => {
  
    const[post,setPost]= useState({});
    //console.log(props);
    useEffect(()=>{
        const PostId=props.match.params.PostId;
        blogpostdata.data.find(post=>post.id==PostId) 
        setPost(post);
    });   
    
  return(
    <div className="blogPostContainer">
        <Card>
            <div className="blogHeader">
            <span className="blogCategory">Feature</span>
            <h1 className="postTitle">Beautiful is always beautiful</h1>
            <span className="postedBy">posted on jully21,2016 bySora Blogging Tips</span>
            </div>
          <div className="PostimageContainer">
            <img src={require('../../blogImages/girl.png')} alt="Post Image"/>
          </div>  
          <div className="postContent">
          <h3>Post Title</h3>
          <p>lorem isgm </p>
          </div>
        </Card>
    </div>   
   )

 }

export default BlogPost;
